function countCharacters(item, previousData) {
  const newData = {
    ...previousData
  };
  const allCharacters = item.title + (item.description || '');
  for (const character of allCharacters) {
    const lowerCase = character.toLowerCase();
    const upperCase = character.toUpperCase();
    if (lowerCase !== upperCase || character === ' ' || character === ',' || character === '.') {
      newData[lowerCase] = {
        lowerCase: (newData[lowerCase]?.lowerCase || 0) + 1,
        upperCase: lowerCase !== upperCase ? (newData[lowerCase]?.upperCase || 0) + 1 : newData[lowerCase]?.upperCase || 0
      };
      newData[upperCase] = {
        lowerCase: newData[upperCase]?.lowerCase || 0,
        upperCase: lowerCase !== upperCase ? (newData[upperCase]?.upperCase || 0) + 1 : newData[upperCase]?.upperCase || 0
      };
    }
  }
  return newData;
}
export { countCharacters };
