function countCharacters(item, previousData) {
  const newData = {
    ...previousData
  };
  const allCharacters = item.title + (item.description || '');
  for (const character of allCharacters) {
    const lowerCase = character.toLowerCase();
    const upperCase = character.toUpperCase();
    if (lowerCase !== upperCase) {
      if (newData[lowerCase]) {
        newData[lowerCase].lowerCase += 1;
        newData[lowerCase].upperCase += 1;
      } else {
        newData[lowerCase] = {
          lowerCase: 1,
          upperCase: 1
        };
      }
      if (newData[upperCase]) {
        newData[upperCase].upperCase += 1;
      } else {
        newData[upperCase] = {
          lowerCase: 0,
          upperCase: 1
        };
      }
    }
  }
  return newData;
}
export { countCharacters };
