/**
 * Sort the items in the given list.
 * 
 * @param {boolean} ascending Should the items be sorted in ascending or descending order?
 * @param {string} sortBy Can be "title", "description", or "status"
 * @param {TodoListItem[]} items to be sorted. Each element of the list contains a "title" (string), an optional "description" (string or undefined) and a numerical status.
 */
function sortItems(ascending, sortBy, items) {
  // TODO Implement a sorting function

  items.sort((item1, item2) => {
    let value1, value2;
    if (sortBy === "title") {
      value1 = item1.title.toLowerCase();
      value2 = item2.title.toLowerCase();
    } else if (sortBy === "description") {
      value1 = item1.description ? item1.description.toLowerCase() : "";
      value2 = item2.description ? item2.description.toLowerCase() : "";
    } else if (sortBy === "status") {
      value1 = item1.status.toLowerCase();
      value2 = item2.status.toLowerCase();
    }
    if (value1 < value2) {
      return ascending ? -1 : 1;
    } else if (value1 > value2) {
      return ascending ? 1 : -1;
    } else {
      // If the values are equal, sort by title in ascending order as a secondary sorting criteria
      const title1 = item1.title.toLowerCase();
      const title2 = item2.title.toLowerCase();
      if (title1 < title2) {
        return -1;
      } else if (title1 > title2) {
        return 1;
      } else {
        return 0;
      }
    }
  });
}
